CREATE TABLE `pecas` (  `partnumber` varchar(100) NOT NULL,  `marca_id` int(6) unsigned NOT NULL,  `puc` varchar(100) NOT NULL,  `pu` varchar(255) DEFAULT NULL,  `moeda_preco` varchar(10) DEFAULT NULL,  `preco` decimal(12,2) NOT NULL,  `preco_fornecedor_id` decimal(12,2) NOT NULL,  `disponibilidade_id` int(1) DEFAULT NULL,  `disponibilidade_fornecedor_id` int(1) DEFAULT NULL,  `oferta` int(1) DEFAULT NULL,  PRIMARY KEY (`partnumber`,`marca_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;